// Re-export everything from @prisma/client
export * from '@prisma/client';

// Manual type definitions to supplement the Prisma client
// These would normally be auto-generated by `prisma generate`
import { Prisma as PrismaNamespace } from '@prisma/client';

export namespace Prisma {
  // Re-export all existing Prisma types
  export import validator = PrismaNamespace.validator;

  // Project types
  export type ProjectCreateInput = {
    id?: string;
    name: string;
    description?: string | null;
    createdAt?: Date | string;
    updatedAt?: Date | string;
    tasks?: any; // TaskCreateNestedManyWithoutProjectInput
  };

  export type ProjectUpdateInput = {
    id?: string | { set?: string };
    name?: string | { set?: string };
    description?: string | null | { set?: string | null };
    createdAt?: Date | string | { set?: Date | string };
    updatedAt?: Date | string | { set?: Date | string };
    tasks?: any; // TaskUpdateManyWithoutProjectNestedInput
  };

  // Task types (Unchecked - uses foreign keys directly)
  export type TaskUncheckedCreateInput = {
    id?: string;
    projectId: string;
    name: string;
    sharedContext: string;
    order?: number;
    createdAt?: Date | string;
    updatedAt?: Date | string;
    subtasks?: any; // SubtaskUncheckedCreateNestedManyWithoutTaskInput
  };

  export type TaskUncheckedUpdateInput = {
    id?: string | { set?: string };
    projectId?: string | { set?: string };
    name?: string | { set?: string };
    sharedContext?: string | { set?: string };
    order?: number | { set?: number };
    createdAt?: Date | string | { set?: Date | string };
    updatedAt?: Date | string | { set?: Date | string };
    subtasks?: any; // SubtaskUncheckedUpdateManyWithoutTaskNestedInput
  };

  // Subtask types (Unchecked - uses foreign keys directly)
  export type SubtaskUncheckedCreateInput = {
    id?: string;
    taskId: string;
    name: string;
    type?: any; // SubtaskType enum
    content: string;
    metadata?: any | null; // JsonNullValueInput
    order?: number;
    createdAt?: Date | string;
    updatedAt?: Date | string;
  };

  export type SubtaskUncheckedUpdateInput = {
    id?: string | { set?: string };
    taskId?: string | { set?: string };
    name?: string | { set?: string };
    type?: any; // SubtaskType enum or update operation
    content?: string | { set?: string };
    metadata?: any | null; // JsonNullValueInput or update operation
    order?: number | { set?: number };
    createdAt?: Date | string | { set?: Date | string };
    updatedAt?: Date | string | { set?: Date | string };
  };
}
